// The Note Web Component
const notes = document.getElementsByTagName("note");
if (notes) {
  for (let x of notes) {
    // Define the Note web component content
    x.innerHTML =
      `<img alt="Note:" title="Note" name="note" class="img-inline" src="assets/note.jpg" />&nbsp;<strong>Note</strong>` +
      x.innerHTML;
  }
}

